var num = 0;

function next(arr) {
    var slider = document.getElementById("slider");
    num++;
    if (num >= arr.length) {
        num = 0;
    }
    slider.src = arr[num];
}

function prev(arr) {
    var slider = document.getElementById("slider");
    num--;
    if (num < 0) {
        num = arr.length - 1;
    }
    slider.src = arr[num];
}
